import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { variables } from '../../utils/api/variables.js';

// Ngưỡng miễn phí giao hàng
export const DELIVERY_THRESHOLD = 1200;

// Thunk để tải cart từ database hoặc localStorage
export const loadCart = createAsyncThunk('cart/loadCart', async () => {
  const isAuthenticated = localStorage.getItem('user').token !== null;

  if (isAuthenticated) {
    const response = await axios.get(`${variables.CART_API}`);
    return response.data.items || []; // Giả sử API trả về { items: [...] }
  } else {
    const localCart = localStorage.getItem('cartItems');
    return localCart ? JSON.parse(localCart) : [];
  }
});

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    subtotal: 0,
    delivery: 0,
    discount: 0,
    total: 0,
  },
  reducers: {
    addToCart: (state, action) => {
      const { product, size, quantity = 1, price } = action.payload;
      const existingItem = state.items.find(
        (item) => item.product.id === product.id && item.size === size
      );

      if (existingItem) {
        existingItem.quantity += quantity;
      } else {
        state.items.push({ product, size, quantity, price });
      }

      const isAuthenticated = localStorage.getItem('user').token !== null;

      if (isAuthenticated) {
        axios
          .post(`${variables.CART_API}`, { items: state.items })
          .then(() => console.log('Cart saved to database'))
          .catch((err) => console.error('Error saving cart:', err));
      } else {
        localStorage.setItem('cartItems', JSON.stringify(state.items));
      }
    },

    removeFromCart: (state, action) => {
      const { product, size } = action.payload;
      state.items = state.items.filter(
        (item) => item.product.id !== product.id || item.size !== size
      );

      const isAuthenticated = localStorage.getItem('user').token !== null;

      if (isAuthenticated) {
        axios
          .put(`${variables.CART_API}`, { items: state.items })
          .then(() => console.log('Cart updated in database'))
          .catch((err) => console.error('Error updating cart:', err));
      } else {
        localStorage.setItem('cartItems', JSON.stringify(state.items));
      }
    },

    updateQuantity: (state, action) => {
      const { productId, size, quantity } = action.payload;
      const cartItemIndex = state.items.findIndex(
        (item) => item.product.id === productId && item.size === size
      );

      if (cartItemIndex !== -1) {
        state.items[cartItemIndex].quantity = quantity;

        const isAuthenticated = localStorage.getItem('user').token !== null;

        if (isAuthenticated) {
          axios
            .put(`${variables.CART_API}`, { items: state.items })
            .then(() => console.log('Cart updated in database'))
            .catch((err) => console.error('Error updating cart:', err));
        } else {
          localStorage.setItem('cartItems', JSON.stringify(state.items));
        }
      }
    },

    clearCart: (state) => {
      state.items = [];

      const isAuthenticated = localStorage.getItem('user').token !== null;

      if (isAuthenticated) {
        axios
          .delete(`${variables.CART_API}`)
          .then(() => console.log('Cart cleared in database'))
          .catch((err) => console.error('Error clearing cart:', err));
      } else {
        localStorage.removeItem('cartItems');
      }
    },

    calculateSubtotal: (state) => {
      const subtotal = state.items.reduce(
        (acc, item) => acc + item.price * item.quantity,
        0
      );

      if (subtotal >= DELIVERY_THRESHOLD) {
        state.delivery = 0;
        state.subtotal = subtotal;
      } else {
        state.delivery = 60;
        state.subtotal = subtotal;
      }
    },

    updateDelivery: (state, action) => {
      state.delivery = action.payload.deliveryCost;
    },

    applyDiscount: (state, action) => {
      state.discount = action.payload.discount;
    },

    getTotal: (state) => {
      state.total = state.subtotal - state.subtotal * state.discount + state.delivery;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(loadCart.fulfilled, (state, action) => {
      state.items = action.payload;
    });
  },
});

export const {
  addToCart,
  removeFromCart,
  updateQuantity,
  clearCart,
  calculateSubtotal,
  updateDelivery,
  applyDiscount,
  getTotal,
} = cartSlice.actions;

export default cartSlice.reducer;
