import axios from 'axios';
import { variables } from './variables.js';
import jwtDecode from 'jwt-decode';

const API_URL = variables.CUSTOMER_API
const API_URL_AUTHEN = variables.AUTHEN_API
const API_URL_CART = variables.CART_API;

const getUsers = async () => {
  const response = await axios.get(API_URL);
  return response.data;
}

const getUser = async (userId) => {
  const response = await axios.get(`${API_URL}/${userId}`);
  return response.data;
}

const createUser = async (user) => {
  const response = await axios.post(API_URL, user);
  return response.data;
}

const updateUser = async (userId, user) => {
  const response = await axios.put(`${API_URL}/${userId}`, user);
  return response.data;
}

const deleteUser = async (userId) => {
  const response = await axios.delete(`${API_URL}/${userId}`);
  return response.data;
}

const getCartByUserId = async (userId) => {
  try {
    const response = await axios.get(`${API_URL_CART}/${userId}`);
    return response.data; 
  } catch (error) {
    console.error('Error fetching cart:', error);
    return null;
  }
};

const login = async (loginData) => {
  try {
    const response = await axios.post(`${API_URL_AUTHEN}/login-customer`, loginData);
    if (response.data.token) {
      // Lưu thông tin người dùng vào localStorage
      localStorage.setItem('user', JSON.stringify(response.data));

      // Lấy userId từ response và lấy cartId của người dùng
      const userId = response.data.id;
      const cart = await getCartByUserId(userId); // Lấy cartId thông qua userId

      if (cart) {
        const cartId = cart.cartId; 
        // Lưu cartId vào localStorage hoặc redux state nếu cần
        localStorage.setItem('cartId', cartId);
      }

      return { userId, cartId: cart ? cart.cartId : null }; 
    }
    return { token: null };
  } catch (error) {
    if (error.response && error.response.status === 401) {
      alert('Invalid email or password');
    } else {
      alert('An error occurred');
    }
    return { token: null };
  }
};

export default {
  login,
  getUser,
  getUsers,
  createUser,
  updateUser,
  deleteUser
};
